import React, { Component } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import Modal from 'react-modal';
import axios from 'axios';
import './List.scss';
library.add(faEdit, faTrash);


class Table extends Component {

  constructor(props){
     super(props);
     this.state = {
      tableData: [],
      modalAdd : false,
    }
  }

  openModalAdd = async () => {
    this.setState({modalAdd:true});
  };

  closeModalAdd = async () => {
    this.setState({modalAdd:false});
  };

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    // subtitle.style.color = '#f00';
  }

  componentDidMount() {
    this.fetchDataTable();
  }

  fetchDataTable = async () => {
    axios.get('http://localhost:5000/users')
    .then(res => {
      const _response = res.data;
      if(_response.status == '00'){
        this.setState({ tableData: _response.data });

      }
    })
  };



  createTable(){
    let i;
    for(i=0;i<this.state.tableData.length;i++){
      console.log(i+1)}
    return this.state.tableData.map((book, index) => {
      const { id, title, author, date, num_page, book_type } = book //destructuring
      if(this.state.tableData.length == 0){
        return(
          <tr>
            <td>Transaksi tidak memiliki diskon</td>
          </tr>
        )
      }else{
          return(
            <tr key={id}>
             <td>{index+1}.</td>
             <td>{author}</td>
             <td>{title}</td>
             <td>{date}</td>
             <td>{num_page}</td>
             <td>{book_type}</td>
             <td>
              <div className="control">
                <FontAwesomeIcon icon="edit" className="iconEdit"/>
              </div>
              <div className="control">
                <FontAwesomeIcon icon="trash" className="iconDelete"/>
              </div>
             </td>
            </tr>
        )
      }})
    return null;
  }


  render() {
    const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };
    return (
      <div>
        <h1>Book List</h1>
        <table id="students">
          <tbody>
           <tr>
            <th>No.</th>
            <th>Author.</th>
            <th>Title.</th>
            <th>Date.</th>
            <th>Number of Page.</th>
            <th>Book Type.</th>
            <th>Option.</th>
           </tr>
           {this.createTable()}
          </tbody>
        </table>
        <div>
        <button onClick={this.openModalAdd}>Open Modal</button>
        <Modal
          isOpen={this.state.modalAdd}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.state.modalAdd}
          style={customStyles}
          contentLabel="Example Modal">

          <h2 id="modal">Add Book</h2>
          <form>
            <div>
              <input id="modal" type="text" placeholder="Book Title"/>
              <input id="modal" type="text" placeholder="Author Name"/>
              <input id="modal" type="date" placeholder="Author Name"/>
              <input id="modal" type="number" placeholder="Total Page"/>
              <input id="modal" type="text" placeholder="Book Type"/>
            </div>
            <div className="buttons">
              <button onClick={this.closeModalAdd}>Cancel</button>
              <input type="submit" value="Submit"/>
            </div>
          </form>
        </Modal>
      </div>
      </div>
    )
  }
}

export default Table
