import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import axios from 'axios';
import './List.scss';


class Table extends Component {
  constructor(props){
     super(props);
     this.state = {
      tableData: [],
    }
  }

  componentDidMount() {
    this.fetchDataTable();
  }

  fetchDataTable = async () => {
    axios.get('http://localhost:5000/users')
    .then(res => {
      const _response = res.data;
      if(_response.status == '00'){
        this.setState({ tableData: _response.data });

      }
    })
  };

  createTable(){
    let i;
    for(i=0;i<this.state.tableData.length;i++){
      console.log(i+1)}
    return this.state.tableData.map((book, index) => {
      const { id, title, author, date, num_page, book_type } = book //destructuring
      if(this.state.tableData.length == 0){
        return(
          <tr>
            <td>Transaksi tidak memiliki diskon</td>
          </tr>
        )
      }else{
          return(
            <tr key={id}>
             <td>{index+1}.</td>
             <td>{author}</td>
             <td>{title}</td>
             <td>{date}</td>
             <td>{num_page}</td>
             <td>{book_type}</td>
             <td><FontAwesomeIcon icon="edit" /> Delete</td>
            </tr>
        )
      }})
    return null;
  }


  render() {
    return (
      <div>
        <h1>Book List</h1>
        <table id="students">
          <tbody>
           <tr>
            <th>No.</th>
            <th>Author.</th>
            <th>Title.</th>
            <th>Date.</th>
            <th>Number of Page.</th>
            <th>Book Type.</th>
            <th>Option.</th>
           </tr>
           {this.createTable()}
          </tbody>
        </table>
      </div>
    )
  }
}

export default Table
