import React, { Component } from 'react';
import axios from 'axios';

class Table extends Component {
  constructor(props){
     super(props);
     this.state = {
      tableData: [],
    }
  }

  componentDidMount() {
    this.fetchDataTable();
  }

  fetchDataTable = async () => {
    axios.get('http://localhost:5000/users')
    .then(res => {
      const _response = res.data;
      if(_response.status == '00'){
        this.setState({ tableData: _response.data });
        console.log(_response.data)
      }
    })
  };

  createTable(){
    if(this.state.tableData.length == 0){
      return(
        <tr>
          <td>Transaksi tidak memiliki diskon</td>
        </tr>
      )
    }else{
      for(let i=1;i<this.state.tableData.length+1;i++){
        let no=i;
        return(
          <tr key={no}>
           <td>{no}</td>
           <td>{this.state.tableData.author}</td>
           <td>{this.state.tableData.title}</td>
           <td>{this.state.tableData.date}</td>
           <td>{this.state.tableData.num_page}</td>
           <td>{this.state.tableData.book_type}</td>
          </tr>
      )}
    }
    return null;
  }

  render() {
    return (
      <table>
         <tbody>
            {this.createTable()}
         </tbody>
      </table>
    )
  }
}

export default Table
