import React, { Component } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons'
import Modal from 'react-modal';
import axios from 'axios';
import './List.scss';
library.add(faEdit, faTrash);


class Table extends Component {

  constructor(props){
     super(props);
     this.state = {
      tableData: [],
      modalAdd : false,
      titleValue: '',
      authorValue: '',
      dateValue: '',
      numpageValue: '',
      booktypeValue: '',
    }
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    console.log(this.state.titleValue);
    console.log(this.state.authorValue);
    console.log(this.state.dateValue);
    console.log(this.state.numpageValue);
    console.log(this.state.booktypeValue);
    event.preventDefault();
    axios.post('http://localhost:5000/add_book', {
      title: this.state.titleValue,
      author: this.state.authorValue,
      date: this.state.dateValue,
      num_page: this.state.numpageValue,
      book_type: this.state.booktypeValue,
    })
    .then(res => {
      if(res.status == '00'){
        console.log('sukses')
      }
    })
    this.setState({modalAdd:false});
    window.location.reload(false);
  }

  openModalAdd = async () => {
    this.setState({modalAdd:true});
  };

  closeModalAdd = async () => {
    this.setState({modalAdd:false});
  };

  afterOpenModal() {
    // references are now sync'd and can be accessed.
    // subtitle.style.color = '#f00';
  }

  componentDidMount() {
    this.fetchDataTable();
  }

  fetchDataTable = async () => {
    axios.get('http://localhost:5000/users')
    .then(res => {
      const _response = res.data;
      if(_response.status == '00'){
        this.setState({ tableData: _response.data });

      }
    })
  };



  createTable(){
    let i;
    for(i=0;i<this.state.tableData.length;i++){
      console.log(i+1)}
    return this.state.tableData.map((book, index) => {
      const { id, title, author, date, num_page, book_type } = book //destructuring
      if(this.state.tableData.length == 0){
        return(
          <tr>
            <td>Transaksi tidak memiliki diskon</td>
          </tr>
        )
      }else{
          return(
            <tr key={id}>
             <td>{index+1}.</td>
             <td>{author}</td>
             <td>{title}</td>
             <td>{date}</td>
             <td>{num_page}</td>
             <td>{book_type}</td>
             <td>
              <div className="control">
                <FontAwesomeIcon icon="edit" className="iconEdit"/>
              </div>
              <div className="control">
                <FontAwesomeIcon icon="trash" className="iconDelete"/>
              </div>
             </td>
            </tr>
        )
      }})
    return null;
  }


  render() {
    const customStyles = {
    content : {
      top                   : '50%',
      left                  : '50%',
      right                 : 'auto',
      bottom                : 'auto',
      marginRight           : '-50%',
      transform             : 'translate(-50%, -50%)'
    }
  };
    return (
      <div>
        <h1>Book List</h1>
        <table id="students">
          <tbody>
           <tr>
            <th>No.</th>
            <th>Author.</th>
            <th>Title.</th>
            <th>Date.</th>
            <th>Number of Page.</th>
            <th>Book Type.</th>
            <th>Option.</th>
           </tr>
           {this.createTable()}
          </tbody>
        </table>
        <div>
        <button onClick={this.openModalAdd}>Open Modal</button>
        <Modal
          isOpen={this.state.modalAdd}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModalAdd}
          ariaHideApp={false}
          style={customStyles}
          contentLabel="Example Modal">
          <h2 id="modal">Add Book</h2>
          <form onSubmit={this.handleSubmit}>
            <div>
              <input id="modal" type="text" placeholder="Book Title" value={this.state.titleValue} onChange={event => this.setState({ titleValue: event.target.value })}/>
              <input id="modal" type="text" placeholder="Author Name" value={this.state.authorValue} onChange={event => this.setState({ authorValue: event.target.value })}/>
              <input id="modal" type="date" placeholder="Author Name" value={this.state.dateValue} onChange={event => this.setState({ dateValue: event.target.value })}/>
              <input id="modal" type="number" placeholder="Total Page" value={this.state.numpageValue} onChange={event => this.setState({ numpageValue: event.target.value })}/>
              <input id="modal" type="text" placeholder="Book Type" value={this.state.booktypeValue} onChange={event => this.setState({ booktypeValue: event.target.value })}/>
            </div>
            <div className="buttons">
              <input className="submit" type="submit" value="Submit"/>
            </div>
            <div className="buttons">
              <button className="cancel" onClick={this.closeModalAdd}>Cancel</button>
            </div>
          </form>
        </Modal>
      </div>
      </div>
    )
  }
}

export default Table
