import React, { Component } from 'react';
import axios from 'axios';

class Table extends Component {
  constructor(props){
     super(props);
     this.state = {
      tableData: [],
      tableNumber: [],
    }
  }

  componentDidMount() {
    this.fetchDataTable();
  }

  fetchDataTable = async () => {
    axios.get('http://localhost:5000/users')
    .then(res => {
      const _response = res.data;
      if(_response.status == '00'){
        this.setState({ tableData: _response.data });
        for(let i=0;i<this.state.tableData.length;i++){
          this.setState({ tableNumber: i+1 });
          // console.log(i+1)
        }
      }
    })
  };

  createTable(){
    console.log(this.state.tableNumber)
    return this.state.tableData.map((book, index) => {
      const { id, title, author, date, num_page, book_type } = book //destructuring
      if(this.state.tableData.length == 0){
        return(
          <tr>
            <td>Transaksi tidak memiliki diskon</td>
          </tr>
        )
      }else{
          return(
            <tr key={id}>
             <td>{author}</td>
             <td>{title}</td>
             <td>{date}</td>
             <td>{num_page}</td>
             <td>{book_type}</td>
            </tr>
        )
      }})

    return null;
  }

  render() {
    return (
      <table>
         <tbody>
            {this.createTable()}
         </tbody>
      </table>
    )
  }
}

export default Table
